// Enhanced email parsing with better error handling
const item = $json;

// Extract headers safely
const headers = item.payload?.headers || [];
const getHeader = (name) => headers.find(h => h.name.toLowerCase() === name.toLowerCase())?.value || '';

const subject = getHeader('subject') || item.subject || 'No Subject';
const fromEmail = getHeader('from') || 'Unknown sender';
const toEmail = getHeader('to') || 'Unknown recipient';
const date = getHeader('date') || new Date().toISOString();

// Extract body content with better handling
let body = '';
let htmlBody = '';

function extractBodyFromPayload(payload) {
  if (payload.body?.data) {
    const decoded = Buffer.from(payload.body.data, 'base64').toString('utf-8');
    if (payload.mimeType === 'text/plain') {
      return { text: decoded, html: '' };
    } else if (payload.mimeType === 'text/html') {
      return { text: '', html: decoded };
    }
  }

  if (payload.parts) {
    let textContent = '';
    let htmlContent = '';

    for (const part of payload.parts) {
      if (part.mimeType === 'text/plain' && part.body?.data) {
        textContent += Buffer.from(part.body.data, 'base64').toString('utf-8');
      } else if (part.mimeType === 'text/html' && part.body?.data) {
        htmlContent += Buffer.from(part.body.data, 'base64').toString('utf-8');
      } else if (part.parts) {
        // Recursive for nested parts
        const nested = extractBodyFromPayload(part);
        textContent += nested.text;
        htmlContent += nested.html;
      }
    }

    return { text: textContent, html: htmlContent };
  }

  return { text: '', html: '' };
}

const bodyContent = extractBodyFromPayload(item.payload);
body = bodyContent.text || bodyContent.html;
htmlBody = bodyContent.html;

// Convert HTML to plain text if needed
if (!bodyContent.text && bodyContent.html) {
  body = bodyContent.html
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/p>/gi, '\n\n')
    .replace(/<[^>]+>/g, '')
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/\s+/g, ' ')
    .trim();
}

// Extract Upwork-specific links
const upworkLinks = {
  proposal: body.match(/https:\/\/www\.upwork\.com\/ab\/proposals[^\s]+/g) || [],
  job: body.match(/https:\/\/www\.upwork\.com\/jobs[^\s]+/g) || [],
  contract: body.match(/https:\/\/www\.upwork\.com\/ab\/contracts[^\s]+/g) || [],
  freelancer: body.match(/https:\/\/www\.upwork\.com\/freelancers[^\s]+/g) || []
};

return [{
  messageId: item.id,
  threadId: item.threadId,
  from: fromEmail,
  to: toEmail,
  subject: subject,
  body: body,
  htmlBody: htmlBody,
  date: date,
  upworkLinks: upworkLinks,
  rawPayload: item.payload // Keep for debugging
}];

// Enhanced data extraction based on email classification
const emailData = $items("Parse Email Content1")[0].json;
const category = $items("Classify Email1")[0].json.message.content.trim();
const body = emailData.body;
const subject = emailData.subject;
const upworkLinks = emailData.upworkLinks;

let extractedData = {
  classification: category,
  priority: 'Medium',
  actionRequired: false,
  extractedInfo: {},
  relevantLinks: [],
  summary: ''
};

// Category-specific data extraction
switch (category) {
  case "Job Invitation":
    extractedData.priority = 'High';
    extractedData.actionRequired = true;
    extractedData.relevantLinks = upworkLinks.proposal.concat(upworkLinks.job);

    // Extract job details
    const budgetMatch = body.match(/\$([,\d]+(?:\.\d{2})?)/g);
    const skillsMatch = body.match(/Skills?:\s*([^\n\r]+)/i);
    const timelineMatch = body.match(/(\d+)\s*(day|week|month)s?/i);

    extractedData.extractedInfo = {
      budget: budgetMatch ? budgetMatch[0] : 'Not specified',
      skills: skillsMatch ? skillsMatch[1].trim() : 'Not specified',
      timeline: timelineMatch ? timelineMatch[0] : 'Not specified'
    };
    extractedData.summary = `New job invitation - Budget: ${extractedData.extractedInfo.budget}`;
    break;

  case "Interview Request":
    extractedData.priority = 'High';
    extractedData.actionRequired = true;
    extractedData.relevantLinks = upworkLinks.proposal;

    const clientMatch = body.match(/([A-Z][a-z]+\s+[A-Z][a-z]+)\s+would like to interview you/i);
    extractedData.extractedInfo = {
      clientName: clientMatch ? clientMatch[1] : 'Not specified'
    };
    extractedData.summary = `Interview request from ${extractedData.extractedInfo.clientName}`;
    break;

  case "Payment Notification":
    extractedData.priority = 'Medium';
    extractedData.actionRequired = false;

    // Better payment detection patterns
    const earningsMatch = body.match(/\$([,\d]+(?:\.\d{2})?)/g);
    const paymentTypeMatch = body.match(/(bonus|milestone|hourly|fixed|payment|earnings|funds)/i);
    const availableMatch = body.match(/available|deposited|transferred|received/i);

    extractedData.extractedInfo = {
      amount: earningsMatch ? earningsMatch[0] : 'Amount not found',
      type: paymentTypeMatch ? paymentTypeMatch[1] : 'Payment',
      status: availableMatch ? 'Available' : 'Processed'
    };
    extractedData.summary = `Payment notification: ${extractedData.extractedInfo.amount} ${extractedData.extractedInfo.type}`;
    break;

  case "Contract Started":
    extractedData.priority = 'High';
    extractedData.actionRequired = true;
    extractedData.relevantLinks = upworkLinks.contract;

    // Better contract title extraction
    const contractTitleMatch = subject.match(/"([^"]+)"/) || body.match(/contract[:\s]+([^\n\r\.]+)/i);
    extractedData.extractedInfo = {
      contractTitle: contractTitleMatch ? contractTitleMatch[1].trim() : 'Contract title not found'
    };
    extractedData.summary = `New contract started: ${extractedData.extractedInfo.contractTitle}`;
    break;

  case "Contract Ended":
    extractedData.priority = 'Medium';
    extractedData.actionRequired = false;
    extractedData.relevantLinks = upworkLinks.contract;

    const endedTitleMatch = subject.match(/"([^"]+)"/) || body.match(/contract[:\s]+([^\n\r\.]+)/i);
    const endReasonMatch = body.match(/(completed|terminated|paused|cancelled|ended)/i);

    extractedData.extractedInfo = {
      contractTitle: endedTitleMatch ? endedTitleMatch[1].trim() : 'Contract title not found',
      reason: endReasonMatch ? endReasonMatch[1] : 'Not specified'
    };
    extractedData.summary = `Contract ${extractedData.extractedInfo.reason}: ${extractedData.extractedInfo.contractTitle}`;
    break;

  case "Client Message":
    extractedData.priority = 'High';
    extractedData.actionRequired = true;

    const messagePreview = body.substring(0, 100).trim();
    extractedData.extractedInfo = {
      preview: messagePreview + (body.length > 100 ? '...' : '')
    };
    extractedData.summary = `New client message: ${extractedData.extractedInfo.preview}`;
    break;

  case "Proposal Submitted":
    extractedData.priority = 'Low';
    extractedData.actionRequired = false;
    extractedData.summary = 'Proposal submission confirmed';
    break;

  case "Profile View":
    extractedData.priority = 'Low';
    extractedData.actionRequired = false;
    extractedData.summary = 'Someone viewed your profile';
    break;

  case "Promotional":
    extractedData.priority = 'Low';
    extractedData.actionRequired = false;
    extractedData.summary = 'Promotional content from Upwork';
    break;

  default:
    extractedData.priority = 'Low';
    extractedData.summary = `Upwork notification: ${category || 'Unclassified'}`;
}

// Add metadata with corrected structure
extractedData.processedAt = new Date().toISOString();
extractedData.emailMetadata = {
  messageId: emailData.messageId,
  threadId: emailData.threadId,
  from: emailData.from,
  subject: emailData.subject,
  date: emailData.date,
  body: emailData.body,
  bodyPreview: emailData.body ? emailData.body.substring(0, 300) : 'No content available'
};

return [extractedData];
